SELECT a.F_ID, a.F_ADDRESS, a.F_CUSTOMER_ID, a.F_NAME, a.F_PHONE, a.F_SALE_TIME, a.F_SALE_USER_ID, a.F_SALE_USER_NAME, a.F_FINANCE_TIME, a.F_FINANCE_USER_ID, a.F_FINANCE_USER_NAME, a.F_SHIPPER_TIME, a.F_SHIPPER_USER_ID, a.F_SHIPPER_USER_NAME, a.F_EXPRESS_ID, a.F_EXPRESS_NAME, a.F_STATE, a.F_MONEY, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_TAX, a.F_TIME, a.F_SALE_INTRO, a.F_FINANCE_INTRO, a.F_SHIPPER_INTRO, a.F_TOWNSHIP, a.F_YAOFNAG, b.F_NAME F_CUSTOMER_NAME, b.F_UNIT FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID where 1=1
and b.F_UNIT like CONCAT("%",#{fName} ,"%")
order by a.F_ID desc LIMIT #{page}, #{rows}

SELECT a.F_ID, a.F_ADDRESS, a.F_CUSTOMER_ID, a.F_NAME, a.F_PHONE, a.F_SALE_TIME, a.F_SALE_USER_ID, a.F_SALE_USER_NAME, a.F_FINANCE_TIME, a.F_FINANCE_USER_ID, a.F_FINANCE_USER_NAME, a.F_SHIPPER_TIME, a.F_SHIPPER_USER_ID, a.F_SHIPPER_USER_NAME, a.F_EXPRESS_ID, a.F_EXPRESS_NAME, a.F_STATE, a.F_MONEY_NOTAX, a.F_MONEY, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_TAX, a.F_TIME, a.F_SALE_INTRO, a.F_FINANCE_INTRO, a.F_SHIPPER_INTRO, a.F_TOWNSHIP, a.F_YAOFNAG, a.F_ISPOLICY, a.F_POLICY_INTRO, b.F_NAME F_CUSTOMER_NAME, b.F_UNIT FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID where a.F_SALE_USER_ID in (${userId}) and a.F_STATE in(0,1,2,3,4,8,9) AND F_ISEDITPRICE in(0,1) and a.F_ISPOLICY != 3
and b.F_UNIT like CONCAT("%",#{fName} ,"%")
order by a.F_ID desc LIMIT #{page}, #{rows}

SELECT a.F_ID, a.F_ADDRESS, a.F_CUSTOMER_ID, a.F_NAME, a.F_PHONE, a.F_SALE_TIME, a.F_SALE_USER_ID, a.F_SALE_USER_NAME, a.F_FINANCE_TIME, a.F_FINANCE_USER_ID, a.F_FINANCE_USER_NAME, a.F_SHIPPER_TIME, a.F_SHIPPER_USER_ID, a.F_SHIPPER_USER_NAME, a.F_EXPRESS_ID, a.F_EXPRESS_NAME, a.F_STATE, a.F_MONEY_NOTAX, a.F_MONEY, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_TAX, a.F_TIME, a.F_SALE_INTRO, a.F_FINANCE_INTRO, a.F_SHIPPER_INTRO, a.F_TOWNSHIP, a.F_YAOFNAG, a.F_ISPOLICY, a.F_POLICY_INTRO, b.F_NAME F_CUSTOMER_NAME, b.F_UNIT FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID where a.F_SALE_USER_ID in (${userId}) and a.F_STATE in(0,1,2,3,4,8,9) AND F_ISEDITPRICE in(0,1) and a.F_ISPOLICY = 3
and b.F_UNIT like CONCAT("%",#{fName} ,"%")
order by a.F_ID desc LIMIT #{page}, #{rows}

select count(1) from t_order where F_SALE_USER_ID in (${userId}) and F_STATE in(0,1,2,3) AND F_ISEDITPRICE in(0,2) AND F_ISPOLICY=0 

	and F_SALE_USER_NAME like CONCAT("%",#{fYwyName} ,"%") 


	and F_TIME between #{beginTime} and #{endTime}

SELECT a.F_ID, a.F_ADDRESS, a.F_CUSTOMER_ID, a.F_NAME, a.F_PHONE, a.F_SALE_TIME, a.F_SALE_USER_ID, a.F_SALE_USER_NAME, a.F_FINANCE_TIME, a.F_FINANCE_USER_ID, a.F_FINANCE_USER_NAME, a.F_SHIPPER_TIME, a.F_SHIPPER_USER_ID, a.F_SHIPPER_USER_NAME, a.F_EXPRESS_ID, a.F_EXPRESS_NAME, a.F_STATE, a.F_MONEY_NOTAX, a.F_MONEY, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_TAX, a.F_TIME, a.F_SALE_INTRO, a.F_FINANCE_INTRO, a.F_SHIPPER_INTRO, a.F_TOWNSHIP, a.F_YAOFNAG, a.F_ISPOLICY, a.F_POLICY_INTRO, b.F_NAME F_CUSTOMER_NAME, b.F_UNIT FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID where a.F_SALE_USER_ID in (${userId}) and a.F_STATE in(0,1,2,3) AND F_ISEDITPRICE in(0,2) AND a.F_ISPOLICY=0
and a.F_SALE_USER_NAME like CONCAT("%",#{fYwyName} ,"%")
and a.F_TIME between #{beginTime} and #{endTime}
order by a.F_ID desc
LIMIT #{page}, #{rows}
