SELECT a.F_ID, a.F_ORDER_ID, a.F_DRUG_ID, a.F_DRUG_NAME, a.F_DRUG_NAME F_NAME, a.F_NUMBER, a.F_NUMBER F_SALES_NUMBER, a.F_PRICE, a.F_GONGYE_PRICE, a.F_KAIPIAO_PRICE, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_MONEY_NOTAX, a.F_MONEY, a.F_XQ_TC, a.F_XQ_TC_MONEY, a.F_DQ_TC, a.F_DQ_TC_MONEY, b.F_BATCH_NUMBER, b.F_SPECIFICATION, b.F_EXPIRY_DATE, b.F_BUYING_PRICE, b.F_ADDRESS FROM t_order_detail a LEFT JOIN t_drug b ON a.F_DRUG_ID=b.F_ID where 1=1
and a.F_ORDER_ID = #{fOrderId}
LIMIT #{page}, #{rows}

SELECT a.F_ORDER_ID, a.F_DRUG_ID F_ID, a.F_DRUG_NAME, a.F_DRUG_NAME F_NAME, a.F_NUMBER, a.F_NUMBER F_SALES_NUMBER, a.F_PRICE, a.F_GONGYE_PRICE, a.F_KAIPIAO_PRICE, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_MONEY_NOTAX, a.F_MONEY, b.F_BATCH_NUMBER, b.F_SPECIFICATION, b.F_EXPIRY_DATE, b.F_BUYING_PRICE, b.F_ADDRESS FROM t_order_detail a LEFT JOIN t_drug b ON a.F_DRUG_ID=b.F_ID where 1=1
and a.F_ORDER_ID = #{fOrderId}
LIMIT #{page}, #{rows}

SELECT a.F_ID, a.F_ORDER_ID, a.F_DRUG_ID, a.F_DRUG_NAME, a.F_NUMBER, a.F_PRICE, a.F_GONGYE_PRICE, a.F_KAIPIAO_PRICE, a.F_MONEY_NOTAX, b.F_BATCH_NUMBER, b.F_SPECIFICATION, b.F_ADDRESS, h.F_NAME F_WAREHOUSE_NAME, c.F_ADDRESS SHOUHUO_ADDRESS, c.F_PHONE, c.F_NAME SHOUHUO_NAME, d.F_UNIT, f.F_NAME PRINTER_NAME FROM t_order_detail a LEFT JOIN t_drug b ON a.F_DRUG_ID=b.F_ID LEFT JOIN t_order c ON a.F_ORDER_ID=c.F_ID LEFT JOIN t_customer d ON c.F_CUSTOMER_ID=d.F_ID LEFT JOIN t_drug_only e ON b.F_DRUG_ONLY_ID=e.F_ID LEFT JOIN t_drug_printer f ON e.F_DRUG_PRINTER_ID=f.F_ID LEFT JOIN t_warehouse h ON b.F_WAREHOUSE_ID=h.F_ID where 1=1
and a.F_ORDER_ID = #{fOrderId}
ORDER BY f.F_ID

SELECT
FROM t_order_detail where 1=1
and F_ORDER_ID = #{fId}

SELECT a.F_ID, a.F_ORDER_ID, a.F_DRUG_ID, a.F_DRUG_NAME, a.F_DRUG_NAME F_NAME, a.F_NUMBER, a.F_NUMBER F_SALES_NUMBER, a.F_PRICE, a.F_GONGYE_PRICE, a.F_KAIPIAO_PRICE, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_MONEY_NOTAX, a.F_MONEY, a.F_XQ_TC, a.F_XQ_TC_MONEY, a.F_DQ_TC, a.F_DQ_TC_MONEY, b.F_BATCH_NUMBER, b.F_SPECIFICATION, b.F_EXPIRY_DATE, b.F_BUYING_PRICE, b.F_ADDRESS, d.F_DEPARTMENT_ID FROM t_order_detail a LEFT JOIN t_drug b ON a.F_DRUG_ID=b.F_ID LEFT JOIN t_drug_only d ON b.F_DRUG_ONLY_ID=d.F_ID where 1=1
and a.F_ORDER_ID = #{fOrderId}
LIMIT #{page}, #{rows}

select count(1) from t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID
LEFT JOIN t_order_detail c ON a.F_ID=c.F_ORDER_ID
where  a.F_ISEDITPRICE in(0,2) 

	and b.F_UNIT like CONCAT("%",#{fName} ,"%") 


	and a.F_SALE_USER_NAME like CONCAT("%",#{fCustomName} ,"%") 


	and a.F_STATE = #{fState}


	and a.F_STATE in(1,2,3) 


	and a.F_PAYMENT_STATE = #{fPaymentState} 


	and a.F_TAX =#{fTax} 


	and a.F_ISPOLICY =#{fIsPolicy} 


	and a.F_TIME between #{beginTime} and #{endTime}

SELECT a.F_ID, a.F_EXAMINE, a.F_ADDRESS, a.F_CUSTOMER_ID, a.F_NAME, a.F_PHONE, a.F_SALE_TIME, a.F_SALE_USER_ID, a.F_SALE_USER_NAME, a.F_FINANCE_TIME, a.F_FINANCE_USER_ID, a.F_FINANCE_USER_NAME, a.F_SHIPPER_TIME, a.F_SHIPPER_USER_ID, a.F_SHIPPER_USER_NAME, a.F_PAYMENT_STATE, a.F_EXPRESS_ID, a.F_EXPRESS_NAME, a.F_STATE, a.F_MONEY_NOTAX, a.F_MONEY_BUYINGPRICE, a.F_MONEY, a.F_GUOJIFEI, a.F_FANDIAN, a.F_GAOKAIFEI, a.F_TAX, a.F_TIME, a.F_SALE_INTRO, a.F_FINANCE_INTRO, a.F_SHIPPER_INTRO, a.F_TOWNSHIP, a.F_YAOFNAG, a.F_ISPOLICY, a.F_POLICY_INTRO, a.F_XQ_TC_MONEY, a.F_DQ_TC_MONEY, b.F_NAME F_CUSTOMER_NAME, b.F_UNIT, c.F_DRUG_ID, c.F_DRUG_NAME, c.F_NUMBER, c.F_GUOJIFEI F_GUOJIFEI_DETAIL, c.F_FANDIAN F_FANDIAN_DETAIL, c.F_GAOKAIFEI F_GAOKAIFEI_DETAIL, c.F_MONEY F_MONEY_DETAIL, c.F_MONEY_BUYINGPRICE F_MONEY_BUYINGPRICE_DETAIL, c.F_XQ_TC_MONEY F_XQ_TC_MONEY_DETAIL, c.F_DQ_TC_MONEY F_DQ_TC_MONEY_DETAIL FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID LEFT JOIN t_order_detail c ON a.F_ID=c.F_ORDER_ID where a.F_ISEDITPRICE in(0,2)
and b.F_UNIT like CONCAT("%",#{fName} ,"%")
and a.F_SALE_USER_NAME like CONCAT("%",#{fCustomName} ,"%")
and a.F_STATE = #{fState}
and a.F_STATE in(1,2,3)
and a.F_PAYMENT_STATE = #{fPaymentState}
and a.F_TAX =#{fTax}
and a.F_ISPOLICY =#{fIsPolicy}
and a.F_TIME between #{beginTime} and #{endTime}
order by a.F_ID desc, c.F_DRUG_ID desc
LIMIT #{page}, #{rows}

SELECT sum(a.F_MONEY_NOTAX) F_MONEY_NOTAX, sum(a.F_MONEY_BUYINGPRICE) F_MONEY_BUYINGPRICE, sum(a.F_MONEY) F_MONEY, sum(a.F_GUOJIFEI) F_GUOJIFEI, sum(a.F_FANDIAN) F_FANDIAN, sum(a.F_GAOKAIFEI) F_GAOKAIFEI, sum(a.F_XQ_TC_MONEY) F_XQ_TC_MONEY, sum(a.F_DQ_TC_MONEY) F_DQ_TC_MONEY FROM t_order a LEFT JOIN t_customer b ON a.F_CUSTOMER_ID=b.F_ID where a.F_ISEDITPRICE in(0,2)
and b.F_UNIT like CONCAT("%",#{fName} ,"%")
and a.F_SALE_USER_NAME like CONCAT("%",#{fCustomName} ,"%")
and a.F_STATE = #{fState}
and a.F_STATE in(1,2,3)
and a.F_PAYMENT_STATE = #{fPaymentState}
and a.F_TAX =#{fTax}
and a.F_ISPOLICY =#{fIsPolicy}
and a.F_TIME between #{beginTime} and #{endTime}
